define("mod_livesonner/countdown",["core/notification"],function(Notification){const formatInterval=seconds=>{seconds=Math.max(0,seconds);const days=Math.floor(seconds/86400);const hours=Math.floor(seconds%86400/3600);const minutes=Math.floor(seconds%3600/60);const parts=[];if(days){parts.push(1===days?"1 dia":days+" dias");}if(hours){parts.push(1===hours?"1 hora":hours+" horas");}if(!days&&!hours){parts.push(minutes<=1?"1 minuto":minutes+" minutos");}return parts.join(" ");};const init=(startTime,selector)=>{const element=document.querySelector(selector);if(!element){return;}const update=()=>{const diff=Math.floor(startTime-Date.now()/1e3);if(diff<=0){element.textContent="";element.classList.add("d-none");clearInterval(interval);return;}try{element.textContent=M.util.get_string("countdownmessage","mod_livesonner",formatInterval(diff));element.classList.remove("d-none");}catch(error){Notification.exception(error);}};update();var interval=setInterval(update,6e4);};return{init:init};});
